import React, { useState } from 'react';
import { useAuth0 } from "@auth0/auth0-react";
import { mongoose } from 'mongoose';
const Form = require('./FormSchema');

function useForm() {
    const [fields, setFields] = useState([{prompt: ''}])
    
    return [fields, setFields]
}

function FormBuilder() {
    const { isAuthenticated, user } = useAuth0()
    const [fields, setFields] = useForm()

    mongoose.connect('mongodb+srv://william:NA2mCsWr05Ae2x3q@cluster0.sfrvsdl.mongodb.net/?retryWrites=true&w=majority')

    async function save(fields) {
        const form = new Form({name: user.email, fields: fields.toString()})
        await form.save().then(() => console.log('Form saved to mongo'))
    }

    const handleChange = (event, index) => {
        let form = [...fields]
        form[index][event.target.name] = event.target.value
        setFields(form)
    }

    const submit = (event) => {
        event.preventDefault()
        console.log(fields)
        save(fields)
    }

    const addFields = () => {
        let field = {prompt: ''}
        setFields([...fields, field])
    }

    const removeFields = (index) => {
        let form = [...fields]
        form.splice(index, 1)
        setFields(form)
    }

    if (isAuthenticated) {
        return (
            <div>
                <p>Form Builder</p>
                <form onSubmit={submit}>
                    {fields.map((form, index) => {
                        return (
                            <div key={index}>
                                <input
                                    name='prompt'
                                    placeholder='Prompt'
                                    onChange={event => handleChange(event, index)}
                                    value={form.prompt}
                                />
                                <button onClick={() => removeFields(index)}>Remove field</button>
                            </div>
                        )
                    })}
                </form>
                <button onClick={addFields}>New field</button>
                <br></br>
                <button onClick={submit}>Submit</button>
            </div>
        )
    }
}

export default FormBuilder;
